package img

import (
	"encoding/base64"
	"fmt"
	"os"
	"testing"
)

func TestSplitImage(t *testing.T) {
	input := ``
	b, _ := base64.StdEncoding.DecodeString(input)

	shards, first, err := Split(b)
	if err != nil {
		panic(err)
	}

	file, err := os.Create("./test.png")
	if err != nil {
		panic(err)
	}

	defer file.Close()
	fmt.Println(shards)
	file.Write(first)
}
